import { makeStyles, createMuiTheme, responsiveFontSizes, ThemeProvider } from '@material-ui/core/styles';
import React, { Component } from "react";
import PropTypes from 'prop-types';
import './info.js';

// Components
import Avatar from '@material-ui/core/Avatar';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Grid from '@material-ui/core/Grid';
import IconButton from '@material-ui/core/IconButton';
import { Link, animateScroll as scroll } from "react-scroll";
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { init } from 'ityped'

// Fonts & Styles
import 'fontsource-roboto';
import './App.css';

// Icons
import CloseIcon from '@material-ui/icons/Close';
import GitHubIcon from '@material-ui/icons/GitHub';
import InstagramIcon from '@material-ui/icons/Instagram';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import TwitterIcon from '@material-ui/icons/Twitter';

// Images
import avatar from './assets/img/profile.jpg';
import hermes_card from './assets/img/hermes_card.png';
import hermes from './assets/img/hermes.png';


const useStyles = makeStyles((theme) => ({
  root: {
  },
  card: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
  sticky: {
    position: "sticky",
    top: "0rem",
  },
  paper: {
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  column: {
    padding: theme.spacing(15),
  },
  navitem: {
    padding: theme.spacing(1.5),
    textAlign: 'center',
  },
  small: {
    width: theme.spacing(3),
    height: theme.spacing(3),
  },
  github: {
    width: theme.spacing(4.1),
    height: theme.spacing(4.8),
    padding: theme.spacing(0, 0.5, 0, 0),
    color: 'black',
  },
  link: {
    color: 'black',
  },
  large: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    color: 'black',
  },
  avatar: {
    width: theme.spacing(25),
    height: theme.spacing(25),
  },
}));

let theme = createMuiTheme();
theme = responsiveFontSizes(theme);

class Typing extends Component {

  componentDidMount() {
    const myElement = document.querySelector("#myElement");
    init(myElement, {
      strings: ["Student", "Developer", "Musician", "Friend"],
      showCursor: false,
    });
  }
  render() {
    return <Typography id="myElement" variant="h4"/>;
  }
}

class Project {

  constructor(title, excerpt, description, image, type, link) {
    this.title = title;
    this.excerpt = excerpt;
    this.description = description;
    this.card_image = image;
    this.type = type;
    this.link = link;
  }
}

function Intro() {

  return (
    <div container align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
      <Grid container direction="column" align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
        <Grid item xs={10}>
          <ThemeProvider theme={theme}>
            <Typography variant="h2">Brenden Smith</Typography>
            <Box pt={1.5}/>
            {/* <Container fixed direction="row" style={{ minHeight: '100px'}}><Typing/></Container> */}
            <Box pt={1.5}/>
            <Typography variant="h5">Student at CSU Long Beach • ASI Senator for College of Engineering • Undergraduate Research Assistant at CSU Long Beach</Typography>
            <Box pt={3}/>
            <Typography variant="subtitle1">C++ • CSS • Dart • HTML • Java • JavaScript • Python • Swift</Typography>
            <Typography variant="subtitle1">Flutter • Firebase • React • SwiftUI</Typography>
          </ThemeProvider>
        </Grid>
      </Grid>
    </div>
  )
}

function ProjectCard(props) {

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const classes = useStyles();

  return (
    <div>
      <Card className={classes.root} onClick={handleClickOpen}>
        <CardActionArea onClick={handleClickOpen}>
          <CardMedia
            className={classes.media}
            image={props.prj.image}
            title={props.prj.title}
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="h2">
              {props.prj.title}
            </Typography>
            <Typography variant="body2" color="textSecondary" component="p">
              {props.prj.excerpt}..
            </Typography>
          </CardContent>
        </CardActionArea>
      </Card>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title" justify="center" align="center" alignItems="center">
          <Grid container direction="row">
            <Grid item xs={1}>
              <IconButton onClick={handleClose} color="primary">
                <CloseIcon/> 
              </IconButton>
            </Grid>
            <Grid item xs={4}/>
            <Grid item xs={2}>
              {props.prj.title}
            </Grid>
            <Grid item xs={5}/>
          </Grid>
          <img src={props.prj.image}/>
        </DialogTitle>
        <DialogContent alignItems="center" justify="center" align="center"> 
          <DialogContentText id="alert-dialog-description">
            {props.prj.excerpt}
            <div/><p> </p>
            {props.prj.description}
          </DialogContentText>
        </DialogContent>
        <DialogActions justify="center" alignItems="center">
          <div style={{flex: '1 0 0'}} />
          <Button onClick={handleClose} color="primary" justify="center" align="center">
            {props.prj.type}
          </Button>
          <div style={{flex: '1 0 0'}} />
        </DialogActions>
      </Dialog>
    </div>
  );
}

function Projects() {

  const classes = useStyles();

  const projectList = {
    Hermes : new Project(
      "Hermes",
      "Hermes is an application that connects students in need of resources to donors willing to provide those resources.", 
      "I worked in a team of four to design this for the Google DSC Solution Challenge 2021. We primarily used Flutter to design the front-end, and Firebase to do the back-end. I specifically worked on the visual aspects of the home view and profile view.", 
      hermes_card,
      hermes,
      "View GitHub",
      "null"),
  }

  return (
    <div container align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
      <Grid container direction="column" align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
        <Grid item xs={10}>
          <Grid container xs={12} spacing={1}>
            <Grid item xs={4}>
              <ProjectCard prj={projectList.Hermes}/>
            </Grid>
            <Grid item xs={4}>
            </Grid>
            <Grid item xs={4}>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </div>
  )
}

function Profile() {

  const classes = useStyles();

  const socials = {
    GitHub: "https://github.com/Brenden-Smith",
    Instagram : "https://instagram.com/brenden.s.smith",
    LinkedIn : "https://www.linkedin.com/in/brenden-s-smith/",
    Twitter : "#",
  };

  return (
    <div container className={classes.sticky} align="center" alignItems="center" justify="center">
      <Grid container direction="column" align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
        <Avatar className={classes.avatar} alt="Brenden Smith" src={avatar}/>
        <Box pt={3}/>
        <Grid container direction="row" justify="center" align="center" spacing={1}>
          <Grid item>
            <a href={socials.GitHub} color="inherit" target="_blank" rel="noopener noreferrer" mx="auto"><GitHubIcon className={classes.github}/></a>
          </Grid>
          <Grid item>
            <a href={socials.Instagram} color="inherit" target="_blank" rel="noopener noreferrer" mx="auto"><InstagramIcon className={classes.large}/></a>
          </Grid>
          <Grid item>
            <a href={socials.LinkedIn} color="inherit" target="_blank" rel="noopener noreferrer"><LinkedInIcon className={classes.large}/></a>
          </Grid>
          <Grid item>
            <a href={socials.Twitter} color="inherit" target="_blank" rel="noopener noreferrer"><TwitterIcon className={classes.large}/></a>
          </Grid>
        </Grid>
        <Box pt={3}/>
        <Grid item className={classes.navitem}>
          <Link to="#home" smooth={true}><Button>About</Button></Link>
        </Grid>
        <Grid item className={classes.navitem}>
          <a href="#projects"><Button>Projects</Button></a>
        </Grid>
        <Grid item className={classes.navitem}>
          <Button>Resume</Button>
        </Grid>
      </Grid>
    </div>
  )
}

function App() {

  const classes = useStyles();

  return (
    <div container className={classes.root}>
      <Grid container align="center" justify="center">
        <Grid item xs={1}/>
        <Grid item xs={2} elevation={3} style={{ minHeight: '100vh' }}>
          <Profile/>
        </Grid>
        <Grid item xs={1}/>
        <Grid item xs={7} m={10}>
          <a id="home"/><Intro/>
          <a id="projects"/><Projects/>
          <a id="resume"/><Intro/>
        </Grid>
        <Grid item xs={1} alignItems="center" >
          <Box pt={1}/>
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
