import { makeStyles, createMuiTheme, responsiveFontSizes, ThemeProvider } from '@material-ui/core/styles';
import React, { Component } from "react";
import PropTypes from 'prop-types';
import './info.js';

// Components
import Avatar from '@material-ui/core/Avatar';
import Badge from '@material-ui/core/Badge';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardContent from '@material-ui/core/CardContent';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import Container from '@material-ui/core/Container';
import Collapse from '@material-ui/core/Collapse';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Grid from '@material-ui/core/Grid';
import IconButton from '@material-ui/core/IconButton';
import { Link, animateScroll as scroll } from "react-scroll";
import Typography from '@material-ui/core/Typography';
import { init } from 'ityped'

// Fonts & Styles
import 'fontsource-roboto';
import './App.css';

// Icons
import CloseIcon from '@material-ui/icons/Close';
import GitHubIcon from '@material-ui/icons/GitHub';
import InstagramIcon from '@material-ui/icons/Instagram';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import TwitterIcon from '@material-ui/icons/Twitter';

// Images
import avatar from './assets/img/profile.jpg';
import hermes from './assets/img/hermes.png';
import journey from './assets/img/journey.png';
import { PinDropSharp } from '@material-ui/icons';


const useStyles = makeStyles((theme) => ({
  prjcard: {
    minHeight: 275,
    maxHeight: 275,
    verticalAlign: 'top',
  },
  card: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
  sticky: {
    position: "sticky",
    top: "0rem",
  },
  paper: {
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  column: {
    padding: theme.spacing(15),
  },
  navitem: {
    padding: theme.spacing(1.5),
    textAlign: 'center',
  },
  small: {
    width: theme.spacing(3),
    height: theme.spacing(3),
  },
  github: {
    width: theme.spacing(4.1),
    height: theme.spacing(4.8),
    padding: theme.spacing(0, 0.5, 0, 0),
    color: 'black',
  },
  link: {
    color: 'black',
  },
  large: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    color: 'black',
  },
  avatar: {
    width: theme.spacing(25),
    height: theme.spacing(25),
  },
}));

let theme = createMuiTheme();
theme = responsiveFontSizes(theme);

class Typing extends Component {

  componentDidMount() {
    const myElement = document.querySelector("#myElement");
    init(myElement, {
      strings: ["Student", "Developer", "Musician", "Friend"],
      showCursor: false,
    });
  }
  render() {
    return <Typography id="myElement" variant="h4"/>;
  }
}

class Project {

  constructor(title, award, excerpt, description, image, type, link) {
    this.title = title;
    this.award = award;
    this.excerpt = excerpt;
    this.description = description;
    this.image = image;
    this.type = type;
    this.link = link;
  }
}

function Intro() {

  return (
    <div container align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
      <Grid container direction="column" align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
        <Grid item xs={10}>
          <ThemeProvider theme={theme}>
            <Typography variant="h2">Brenden Smith</Typography>
            <Box pt={1.5}/>
            {/* <Container fixed direction="row" style={{ minHeight: '100px'}}><Typing/></Container> */}
            <Box pt={1.5}/>
            <Typography variant="h5">Student at CSU Long Beach • ASI Senator for College of Engineering • Undergraduate Research Assistant at CSU Long Beach</Typography>
            <Box pt={3}/>
            <Typography variant="subtitle1">C++ • CSS • Dart • HTML • Java • JavaScript • Python • Swift</Typography>
            <Typography variant="subtitle1">Flutter • Firebase • React • SwiftUI</Typography>
          </ThemeProvider>
        </Grid>
      </Grid>
    </div>
  )
}

function ProjectCard(props) {

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const classes = useStyles();

  var _awardVisible = false;

  if (props.prj.award!="") {
    _awardVisible = true;
  }

  return (
    <div>
      <Badge badgeContent={5} color="red">
        <Card className={classes.prjcard} onClick={handleClickOpen} align="center">
          <CardActionArea className={classes.prjcard} onClick={handleClickOpen} align="center">
              <CardMedia
                className={classes.media}
                image={props.prj.image}
                title={props.prj.title}
              />
            <CardContent>
              <Typography gutterBottom variant="h5" component="h2">
                {props.prj.title}
              </Typography>
              <Typography variant="body2" color="textSecondary" component="p">
                {props.prj.excerpt.substring(0, 75)}...
              </Typography>
            </CardContent>
          </CardActionArea>
        </Card>
        <Dialog
          maxWidth="sm"
          open={open}
          onClose={handleClose}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogTitle id="alert-dialog-title">
            <IconButton onClick={handleClose} color="black">
              <CloseIcon/> 
            </IconButton>   
          </DialogTitle>
          <DialogContent alignItems="center" justify="center" align="center"> 
            <DialogContentText id="alert-dialog-description">
              <img className={classes.media}src={props.prj.image}/>
              <div/><p> </p>
              <Typography variant="h5" style={{ color: 'black' }}>{props.prj.title}</Typography>
              <div/><p> </p>
              <Collapse in={_awardVisible}>
                <div/><p> </p>
                  <p style={{color: 'black'}}>{props.prj.award}</p>
                <div/><p> </p>
              </Collapse>
              {props.prj.excerpt}.
              <div/><p> </p>
              {props.prj.description}
            </DialogContentText>
          </DialogContent>
          <DialogActions justify="center" alignItems="center">
            <div style={{flex: '1 0 0'}} />
            <Button href={props.prj.link} target="_blank" color="primary" justify="center" align="center">
              {props.prj.type}
            </Button>
            <div style={{flex: '1 0 0'}} />
          </DialogActions>
        </Dialog>
      </Badge>
    </div>
  );
}

function Projects() {

  const classes = useStyles();

  const projectList = {
    Hermes : new Project(
      "Hermes",
      "",
      "Hermes is an application that connects students in need of resources to donors willing to provide those resources", 
      "I worked in a team of four to design this for the Google DSC Solution Challenge 2021. We primarily used Flutter to design the front-end, and Firebase to do the back-end. I specifically worked on the visual aspects of the home view and profile view.", 
      hermes,
      "View GitHub",
      "https://github.com/danieljo09/Hermes"),
    Journey: new Project(
      "Journey",
      "MarinaHacks 2021 Winner - Mental Health",
      "Journey is an application designed to help users keep track of their moods and thoughts",
      "I worked in a team of four to design this for MarinaHacks 2021. We designed this app with Flutter, Dart, YouTube API, and Zen Quotes API. Specifically, I did both front and back-end work on this project, including implementing the quotes API and working with the data structures.",
      journey,
      "View GitHub",
      "https://github.com/Brenden-Smith/Journey",
    )
  }

  return (
    <div container align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
      <Grid container direction="column" align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
        <Grid item xs={10}>
          <Grid container xs={12} spacing={3}>
            <Grid item xs={2}/>
            <Grid item xs={4}>
              <ProjectCard prj={projectList.Hermes}/>
            </Grid>
            <Grid item xs={4}>
              <ProjectCard prj={projectList.Journey}/>
            </Grid>
            <Grid item xs={2}/>
          </Grid>
        </Grid>
      </Grid>
    </div>
  )
}

function Profile() {

  const classes = useStyles();

  const socials = {
    GitHub: "https://github.com/Brenden-Smith",
    Instagram : "https://instagram.com/brenden.s.smith",
    LinkedIn : "https://www.linkedin.com/in/brenden-s-smith/",
    Twitter : "#",
  };

  return (
    <div container className={classes.sticky} align="center" alignItems="center" justify="center">
      <Grid container direction="column" align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
        <Avatar className={classes.avatar} alt="Brenden Smith" src={avatar}/>
        <Box pt={3}/>
        <Grid container direction="row" justify="center" align="center" spacing={1}>
          <Grid item>
            <a href={socials.GitHub} color="inherit" target="_blank" rel="noopener noreferrer" mx="auto"><GitHubIcon className={classes.github}/></a>
          </Grid>
          <Grid item>
            <a href={socials.Instagram} color="inherit" target="_blank" rel="noopener noreferrer" mx="auto"><InstagramIcon className={classes.large}/></a>
          </Grid>
          <Grid item>
            <a href={socials.LinkedIn} color="inherit" target="_blank" rel="noopener noreferrer"><LinkedInIcon className={classes.large}/></a>
          </Grid>
          <Grid item>
            <a href={socials.Twitter} color="inherit" target="_blank" rel="noopener noreferrer"><TwitterIcon className={classes.large}/></a>
          </Grid>
        </Grid>
        <Box pt={3}/>
        <Grid item className={classes.navitem}>
          <a href="#home" smooth={true}><Button>About</Button></a>
        </Grid>
        <Grid item className={classes.navitem}>
          <a href="#projects"><Button>Projects</Button></a>
        </Grid>
        <Grid item className={classes.navitem}>
          <a href="#resume"><Button>Resume</Button></a>
        </Grid>
      </Grid>
    </div>
  )
}

function App() {

  const classes = useStyles();

  return (
    <div container className={classes.root}>
      <Grid container align="center" justify="center">
        <Grid item xs={1}/>
        <Grid item xs={2} elevation={3} style={{ minHeight: '100vh' }}>
          <Profile/>
        </Grid>
        <Grid item xs={1}/>
        <Grid item xs={7} m={10}>
          <a id="home"/><Intro/>
          <a id="projects"/><Projects/>
          <a id="resume"/><Intro/>
        </Grid>
        <Grid item xs={1} alignItems="center" >
          <Box pt={1}/>
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
