import { makeStyles, createMuiTheme, responsiveFontSizes, ThemeProvider } from '@material-ui/core/styles';
import React, { Component } from "react";
import './info.js';

// Components
import Avatar from '@material-ui/core/Avatar';

import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Link from '@material-ui/core/Link';
import Typography from '@material-ui/core/Typography';
import { init } from 'ityped'

// Fonts
import 'fontsource-roboto';

// Icons
import GitHubIcon from '@material-ui/icons/GitHub';
import InstagramIcon from '@material-ui/icons/Instagram';
import LinkedInIcon from '@material-ui/icons/LinkedIn';

// Images
import avatar from './assets/img/profile.jpg';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,

  },
  column: {
    padding: theme.spacing(15),
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  small: {
    width: theme.spacing(3),
    height: theme.spacing(3),
  },
  medium: {
    width: theme.spacing(4),
    height: theme.spacing(4.5),
  },
  large: {
    width: theme.spacing(5),
    height: theme.spacing(5),
  },
  xl: {
    width: theme.spacing(25),
    height: theme.spacing(25),
  },
}));

let theme = createMuiTheme();
theme = responsiveFontSizes(theme);

class Typing extends Component {

  componentDidMount() {
    const myElement = document.querySelector("#myElement");
    init(myElement, {
      showCursor: false,
      strings: ["Student", "Developer", "Musician", "Friend"]
    });
  }
  render() {
    return <div id="myElement"></div>;
  }
}

function Intro() {

  return (
    <>
      <ThemeProvider theme={theme}>
        <Typography variant="h2">Brenden Smith</Typography>
      </ThemeProvider>
      <Box pt={3}/>
      {/* <Typing/> */}
      <Box pt={5}/>
      <ThemeProvider theme={theme}>
        <Typography variant="h5">Student at CSU Long Beach • Director of Public Relations & Director of Philanthropy at Sigma Chi, Eta Beta • ASI Senator for College of Engineering</Typography>
      </ThemeProvider>
    </>
  )
}

function Profile() {

  const classes = useStyles();

  const socials = {
    GitHub: "https://github.com/Brenden-Smith",
    Instagram : "https://instagram.com/brenden.s.smith",
    LinkedIn : "https://www.linkedin.com/in/brenden-s-smith/",
  };

  return (
    <>
      {/* <Paper className={classes.paper}>Profile</Paper> */}
      <Avatar className={classes.xl} alt="Brenden Smith" src={avatar}/>
      <Box pt={3}/>
      <Grid container direction="row" justify="center" align="center" spacing={1}>
        <Grid item>
          <Link href={socials.GitHub} color="inherit" target="_blank" rel="noopener noreferrer"><GitHubIcon className={classes.medium}/></Link>
        </Grid>
        <Grid item>
          <Box pt={1}/>
        </Grid>
        <Grid item>
          <Link href={socials.Instagram} color="inherit" target="_blank" rel="noopener noreferrer"><InstagramIcon className={classes.large}/></Link>
        </Grid>
        <Grid item>
          <Link href={socials.LinkedIn} color="inherit" target="_blank" rel="noopener noreferrer"><LinkedInIcon className={classes.large}/></Link>
        </Grid>
        <Grid item>
          <Avatar/>
        </Grid>
        <Grid item>
          <Avatar/>
        </Grid>
      </Grid>
      <p>About</p>
      <p>Projects</p>
      <p>Resume</p>
    </>
  )
}

function App() {
  const classes = useStyles();

  return (
    <div container className={classes.root}>
      <Grid container spacing={3} className={classes.column} align="center" justify="center" style={{
        position: 'absolute', left: '50%', top: '50%',
        transform: 'translate(-50%, -50%)'
    }}>
        <Grid item xs={3}>
          <Box pt="auto"/>
          <Profile/>
          <Box pt="auto"/>
        </Grid>
        <Grid item xs={1}>
          <Box pt={1}/>
        </Grid>
        <Grid item xs={8} m={10}>
          <Box pt={10}/>
          <Intro/>
          <Box pt="auto"/>
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
