import { makeStyles, createMuiTheme, responsiveFontSizes, ThemeProvider } from '@material-ui/core/styles';
import React, { Component } from "react";
import { StickyContainer, Sticky } from 'react-sticky';
import './info.js';

// Components
import Avatar from '@material-ui/core/Avatar';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Link from '@material-ui/core/Link';
// import { Link, animateScroll as scroll } from "react-scroll";
import Typography from '@material-ui/core/Typography';
import { init } from 'ityped'

// Fonts
import 'fontsource-roboto';

// Icons
import GitHubIcon from '@material-ui/icons/GitHub';
import InstagramIcon from '@material-ui/icons/Instagram';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import TwitterIcon from '@material-ui/icons/Twitter';

// Images
import avatar from './assets/img/profile.jpg';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,

  },
  column: {
    padding: theme.spacing(15),
  },
  navitem: {
    padding: theme.spacing(1.5),
    textAlign: 'center',
  },
  small: {
    width: theme.spacing(3),
    height: theme.spacing(3),
  },
  github: {
    width: theme.spacing(4.1),
    height: theme.spacing(4.8),
    padding: theme.spacing(0, 0.5, 0, 0),
  },
  large: {
    width: theme.spacing(5),
    height: theme.spacing(5),
  },
  avatar: {
    width: theme.spacing(25),
    height: theme.spacing(25),
  },
}));

let theme = createMuiTheme();
theme = responsiveFontSizes(theme);

class Typing extends Component {

  componentDidMount() {
    const myElement = document.querySelector("#myElement");
    init(myElement, {
      showCursor: false,
      strings: ["Student", "Developer", "Musician", "Friend"]
    });
  }
  render() {
    return <div id="myElement"></div>;
  }
}

function Intro() {

  return (
    <>
      <ThemeProvider theme={theme}>
        <Typography variant="h2">Brenden Smith</Typography>
        <Box pt={3}/>
        <Typography variant="h5">Student at CSU Long Beach • ASI Senator for College of Engineering • Undergraduate Research Assistant at CSU Long Beach</Typography>
        <Box pt={3}/>
        <Typography variant="subtitle1">C++ • CSS • Dart • HTML • Java • JavaScript • Python • Swift</Typography>
        <Typography variant="subtitle1">Flutter • Firebase • React • SwiftUI</Typography>
      </ThemeProvider>

      {/* <Typing/> */}
    </>
  )
}

function Profile() {

  const classes = useStyles();

  const socials = {
    GitHub: "https://github.com/Brenden-Smith",
    Instagram : "https://instagram.com/brenden.s.smith",
    LinkedIn : "https://www.linkedin.com/in/brenden-s-smith/",
    Twitter : "#",
  };

  return (
    <>
      <StickyContainer>
        <Sticky isSt>
          <Avatar className={classes.avatar} alt="Brenden Smith" src={avatar}/>
          <Box pt={3}/>
          <Grid container direction="row" justify="center" align="center" spacing={1}>
            <Grid item>
              <Link href={socials.GitHub} color="inherit" target="_blank" rel="noopener noreferrer" mx="auto"><GitHubIcon className={classes.github}/></Link>
            </Grid>
            <Grid item>
              <Link href={socials.Instagram} color="inherit" target="_blank" rel="noopener noreferrer" mx="auto"><InstagramIcon className={classes.large}/></Link>
            </Grid>
            <Grid item>
              <Link href={socials.LinkedIn} color="inherit" target="_blank" rel="noopener noreferrer"><LinkedInIcon className={classes.large}/></Link>
            </Grid>
            <Grid item>
              <Link href={socials.Twitter} color="inherit" target="_blank" rel="noopener noreferrer"><TwitterIcon className={classes.large}/></Link>
            </Grid>
          </Grid>
          <Box pt={3}/>
          <Grid item className={classes.navitem}>
            <Button>About</Button>
          </Grid>
          <Grid item className={classes.navitem}>
            <Link to="projects"><Button>Projects</Button></Link>
          </Grid>
          <Grid item className={classes.navitem}>
            <Button>Resume</Button>
          </Grid>
        </Sticky>
      </StickyContainer>
    </>
  )
}

class App extends React.Component {

  render() {

    const classes = useStyles();

    return (
      <div container className={classes.root}>
        <Grid container spacing={2} direction="row" alignItems="center" align="center" justify="center" style={{ minHeight: '100vh' }}>
          <Grid item xs={2}>
            <Profile/>
          </Grid>
          <Grid item xs={1}>
            <Box pt={1}/>
          </Grid>
          <Grid item xs={7} m={10}>
            <Intro/>
          </Grid>
          <Grid item xs={1}>
            <Box pt={1}/>
          </Grid>
        </Grid>
      </div>
    );
  }
}

export default App;
