import React, { Component } from "react";

// Fonts & Styles
import 'fontsource-roboto';
import '../App.css';
import useStyles from '../assets/styles/styles';

// Components
import Avatar from '@material-ui/core/Avatar';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import { Link, animateScroll as scroll } from "react-scroll";

// Icons
import GitHubIcon from '@material-ui/icons/GitHub';
import InstagramIcon from '@material-ui/icons/Instagram';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import TwitterIcon from '@material-ui/icons/Twitter';

// Images
import avatar from '../assets/img/profile.jpg';

// Files
import resume from './assets/pdf/Brenden_Smith_Resume.pdf';

function Menu() {

    const classes = useStyles();

    const socials = {
      GitHub: "https://github.com/Brenden-Smith",
      Instagram : "https://instagram.com/brenden.s.smith",
      LinkedIn : "https://www.linkedin.com/in/brenden-s-smith/",
      Twitter : "https://twitter.com/BrendenSSmith",
    };

    return (
      <div container className={classes.sticky} align="center" alignItems="center" justify="center">
        <Grid container direction="column" align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
          <Avatar className={classes.avatar} alt="Brenden Smith" src={avatar}/>
          <Box pt={3}/>
          <Grid container direction="row" justify="center" align="center" spacing={1}>
            <Grid item>
              <a href={socials.GitHub} color="inherit" target="_blank" rel="noopener noreferrer" mx="auto"><GitHubIcon className={classes.github}/></a>
            </Grid>
            <Grid item>
              <a href={socials.Instagram} color="inherit" target="_blank" rel="noopener noreferrer" mx="auto"><InstagramIcon className={classes.large}/></a>
            </Grid>
            <Grid item>
              <a href={socials.LinkedIn} color="inherit" target="_blank" rel="noopener noreferrer"><LinkedInIcon className={classes.large}/></a>
            </Grid>
            <Grid item>
              <a href={socials.Twitter} color="inherit" target="_blank" rel="noopener noreferrer"><TwitterIcon className={classes.large}/></a>
            </Grid>
          </Grid>
          <Box pt={3}/>
          <Grid item className={classes.navitem}>
            <Link to="home" smooth={true}><Button>About</Button></Link>
          </Grid>
          <Grid item className={classes.navitem}>
            <Link to="projects" smooth={true}><Button>Projects</Button></Link>
          </Grid>
          <Grid item className={classes.navitem}>
            <Button variant="contained" href={resume} target="_blank" rel="noopener noreferrer">View Resume</Button>
          </Grid>
        </Grid>
      </div>
    )
  }

  export default Menu;
