import { makeStyles, createMuiTheme, responsiveFontSizes, ThemeProvider } from '@material-ui/core/styles';
import React, { Component } from "react";

// Components
import Avatar from '@material-ui/core/Avatar';
import Badge from '@material-ui/core/Badge';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardContent from '@material-ui/core/CardContent';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import Container from '@material-ui/core/Container';
import Collapse from '@material-ui/core/Collapse';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Grid from '@material-ui/core/Grid';
import IconButton from '@material-ui/core/IconButton';
import { Link, animateScroll as scroll } from "react-scroll";
import Typography from '@material-ui/core/Typography';
import { init } from 'ityped'

// Views
import Menu from './sections/Menu';
import Projects from './sections/Projects';

// Fonts & Styles
import 'fontsource-roboto';
import './App.css';



// Images
import avatar from './assets/img/profile.jpg';


// PDF
import resume from './assets/pdf/Brenden_Smith_Resume.pdf';


const useStyles = makeStyles((theme) => ({
  prjcard: {
    minHeight: 275,
    maxHeight: 275,
    verticalAlign: 'top',
  },
  badge: {
    backgroundColor: "#FFD700",
    color: "black",
    fontSize: 16,
    fontWeight: 'bold',
  },
  badgeMargin: {
    margin: theme.spacing.unit * 2,

  },

  card: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
  sticky: {
    position: "sticky",
    top: "0rem",
  },
  paper: {
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  column: {
    padding: theme.spacing(15),
  },
  navitem: {
    padding: theme.spacing(1.5),
    textAlign: 'center',
  },
  small: {
    width: theme.spacing(3),
    height: theme.spacing(3),
  },
  github: {
    width: theme.spacing(4.1),
    height: theme.spacing(4.8),
    padding: theme.spacing(0, 0.5, 0, 0),
    color: 'black',
  },
  link: {
    color: 'black',
  },
  large: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    color: 'black',
  },
  avatar: {
    width: theme.spacing(25),
    height: theme.spacing(25),
  },
}));

let theme = createMuiTheme();
theme = responsiveFontSizes(theme);

class Typing extends Component {

  componentDidMount() {
    const myElement = document.querySelector("#myElement");
    init(myElement, {
      strings: ["Student", "Developer", "Musician", "Friend"],
      showCursor: false,
    });
  }
  render() {
    return <Typography id="myElement" variant="h4"/>;
  }
}

function Intro() {

  return (
    <div container align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
      <Grid container direction="column" align="center" alignItems="center" justify="center" style={{ minHeight: '100vh' }}>
        <Grid item xs={10}>
          <ThemeProvider theme={theme}>
            <Typography variant="h2">Brenden Smith</Typography>
            <Box pt={1.5}/>
            {/* <Container fixed direction="row" style={{ minHeight: '100px'}}><Typing/></Container> */}
            <Box pt={1.5}/>
            <Typography variant="h5">Student at CSU Long Beach • ASI Senator for College of Engineering • Undergraduate Research Assistant at CSU Long Beach</Typography>
            <Box pt={3}/>
            <Typography variant="subtitle1">C++ • CSS • Dart • HTML • Java • JavaScript • Python • Swift</Typography>
            <Typography variant="subtitle1">Flutter • Firebase • React • SwiftUI</Typography>
          </ThemeProvider>
        </Grid>
      </Grid>
    </div>
  )
}

function App() {

  const classes = useStyles();

  return (
    <div container className={classes.root}>
      <Grid container align="center" justify="center">
        <Grid item xs={1}/>
        <Grid item xs={2} elevation={3} style={{ minHeight: '100vh' }}>
          <Menu/>
        </Grid>
        <Grid item xs={1}/>
        <Grid item xs={7} m={10}>
          <a id="home"/><Intro/>
          <a id="projects"/><Projects/>
        </Grid>
        <Grid item xs={1} alignItems="center" >
          <Box pt={1}/>
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
